function nt2_write(path, data, sep)
    sep = sep or ','
    local file = assert(io.open(path, "w"))
    file:write('Image ID' .. "," .. 'Caption')
    file:write('\n')
    for k, v in pairs(data) do
      file:write(v["image_id"] .. "," .. v["caption"])
      file:write('\n')
    end
    file:close()
end

