function toBits(num)
    local t = "" -- will contain the bits
    while num > 0 do
        rest = math.fmod(num, 2)
        t = t .. rest
        num = (num - rest) / 2
    end
    while string.len(t) < 16 do
        t = t .. "0"
    end
    return string.reverse(t)
end

function toBits2(num, bts)
    local t = "" -- will contain the bits
    while num > 0 do
        rest = math.fmod(num, 2)
        t = t .. rest
        num = (num - rest) / 2
    end
    while string.len(t) < bts do
        t = t .. "0"
    end
    return string.reverse(t)
end

function toInt(bin)
    return tonumber(bin, 2)
end

function roll()
    u()
    d()
end

function toHex(int)
    return string.format("%x", int)
end

function lead(n)
    while string.len(n) < 2 do
        n = " " .. n
    end
    return n
end

function readhalfword(addr)
    low = memory.readbyte(addr)
    high = memory.readbyte(addr + 1)
    joined = toBits2(high, 8) .. toBits2(low, 8)
    return toInt(joined)
end

function readhalfbyte(addr, h)
    bt = memory.readbyte(addr)
    bst = toBits2(bt, 8)

    if (h == "h") then
        return toInt(string.sub(bst, 0, 3))
    end
    return toInt(string.sub(bst, 4, 7))

end
function text(x, y, s)
    gui.text(x, y, s)
end

function calc_perc()
    perc = math.ceil(100 * (hp + att + def + spe + spa + spd_iv) / (6 * 31))

end

function check_end_script()
    if shiny < 8 or perc > 90 or (hp == 31 and spa == 31 and att == 31) then
        done()
    end
	if ev_grind and ev_cnt>255 then
		done()
	end
end

function read2bytei(addr)
    return toInt(read2byte(addr))
end
function text(x, y, s)
    gui.text(x, y, s)
end

function stat_mod(n)
    str = "+"
    if n < 6 then
        str = " "
    end
    if n == 6 then
        return " "
    end
    o = n - 6
    return str .. o
end

function get_attack_name(id)
    if id > 0 then
        a = attacks[id]
        if a == nil then
            return ""
        else
            return a
        end
    end
    return ""
end

function get_ability_name(id)
    if id > 0 then
        return abilities[id]
    end
    return ""
end
function get_nature_name(id)
    if id > 0 then
        return natures[id]
    end
    return ""
end
function get_item_name(id)
    if id < 537 then
        return items[id + 1]
    else
        return ""
    end
end
function getStat(st, ed)
    return toInt(string.sub(stats, st, ed))
end
function read2byte(addr)
    ls = memory.readbyterange(addr, 2)
    return toBits2(ls[2], 8) .. toBits2(ls[1], 8)
end
function read4byte(addr)
    ls = memory.readbyterange(addr, 4)
    return toBits2(ls[4], 8) .. toBits2(ls[3], 8) .. toBits2(ls[2], 8) .. toBits2(ls[1], 8)
end
function lead3(n)
    while string.len(n) < 3 do
        n = " " .. n
    end
    return n
end

function get_nature_mod(nat, sid)
    if nat == 0 or nat == nil or nat == "" then
        return " "
    end
    s = nature_stat[nat]
    incr = s[1]
    decr = s[2]
    if incr == sid then
        return "+"
    elseif decr == sid then
        return "-"
    else
        return " "
    end

end

function draw_stat(lbl, iv, raw, nat, d, sid)
    return lbl .. ": " .. lead(iv) .. " : " .. get_nature_mod(nat, sid) .. lead3(raw) .. stat_mod(d)
end

function table_str(tab)
    return tab.toString()
end

function get_psn_burn(ind, tox)
    if (ind >= 1 and ind <= 5) then
        return "Sleep " .. ind .. " turns"
    end
    if (ind == 16) then
        return "Burn"
    elseif (ind == 8) then
        return "Poison"
    elseif (ind == 32) then
        return "Frozen"
    elseif (ind == 64) then
        return "Paralyzed"
    elseif (ind == 128) then
        return "Toxic L" .. tox

    else
        return ind
    end
end
function get_statuses(ts_arr)
    local ar = {}

    if (ts_arr[4] > 0) then
        table.insert(ar, "Confused " .. ts_arr[4] .. " turns")
    end
    if (ts_arr[5] > 0) then
        table.insert(ar, "Bound " .. ts_arr[5] / 32 .. " turns")
    end
        if (ts_arr[6] > 0) then
        table.insert(ar, "Infatuated")
    end
    if (ts_arr[7] > 0) then
        table.insert(ar, "Cursed")
    end
    return ar
end
