function frames(frames)
    i = 0
    while i < frames do
        draw()
        gui.text(150, 150, i)
        i = i + 1
    end
end



function reverse(int)
    toInt(string.reverse(toBits(int)))
end

prevState = 0

function inBattle()
	local blank =memory.readbyte(screen_blank)
    return  blank== 0
end


function click(cx, cy)
    stylus.set({ x = cx, y = cy, touch = true })
    frames(20)
    stylus.set({ x = cx, y = cy, touch = false })
    emu.frameadvance()
end

function runAway()
    check_end_script()
    while inBattle() do

        click(120, 165)
        frames(4)

    end
    cnt = cnt + 1
end

function getStat(st, ed)
