function ev_kill()

	local enemy = read2bytei(en_ptr)
	for _, value in pairs(target_kill) do
		local num = value[1]
		if enemy == num then

			while inBattle() do
    --set_attacks()

			click(45, 50)
			end
			ev_cnt = ev_cnt+value[2]

			cnt=cnt+1
		end
    end
	while inBattle() do
		runAway()
	end
end
function lvl_kill()


	while inBattle() do
    set_attacks()

	click(45, 50)
	end

			cnt=cnt+1
end

